import React, {useState} from 'react';
import Meals from "./components/Meals/Meals";

//simulate a group of food data
const MEALS_DATA = [
    {
        id: '1',
        title: 'Beef Noodle',
        desc: 'Purely 100% hand-made, Shaanxi Belt Noodles boast a delightful chewy texture.',
        price: 12.99,
        img: '/img/meals/1.png'
    },
    {
        id: '2',
        title: 'Dan Dan Noodle',
        desc: 'Purely 100% hand-made, Shaanxi Belt Noodles boast a delightful chewy texture.',
        price: 10.99,
        img: '/img/meals/2.png'
    },
    {
        id: '3',
        title: 'Belt Noodle',
        desc: 'Purely 100% hand-made, Shaanxi Belt Noodles boast a delightful chewy texture.',
        price: 13.99,
        img: '/img/meals/3.png'
    },
    {
        id: '4',
        title: 'Rou Jia Mo',
        desc: 'Purely 100% hand-made, Shaanxi Belt Noodles boast a delightful chewy texture.',
        price: 11.99,
        img: '/img/meals/4.png'
    },
    {
        id: '5',
        title: 'Spicy Chicken',
        desc: 'Spicy Chicken is a famous dish in China. It is spicy and delicious. Very good!',
        price: 14.99,
        img: '/img/meals/5.png'
    },
    {
        id: '6',
        title: 'Shui Zhu Pork',
        desc: 'Shui Zhu Pork is a famous dish in China. It is spicy and delicious. Very good!',
        price: 15.99,
        img: '/img/meals/6.png'
    },
    {
        id: '7',
        title: 'Liang Ban Beef',
        desc: 'Liang Ban Beef is a famous dish in China. It is spicy and delicious. Very good!',
        price: 12.99,
        img: '/img/meals/7.png'
    }
];

const App = () => {

    // Create a state to store the food data
    const [mealsData, setMealsData] = useState(MEALS_DATA);

    // Create a state to store the food data in the shopping cart
    /*
        1. food data [], items
        2. total amount
        3. total price
     */
    const [cartData, setCartData] = useState({
        items: [],
        totalAmount: 0,
        totalPrice: 0
    });

    // Create a function to add food to the shopping cart
    const addMealHandler = (meal) => {
        const newCart = {...cartData};
        if (newCart.items.indexOf(meal) === -1) {
            newCart.items.push(meal);
            meal.amount = 1;
        } else {
            meal.amount++;
        }

        newCart.totalAmount++;
        newCart.totalPrice += meal.price;


    };

    return (
        <div>
            <Meals mealsData={mealsData}
                onAdd={addMealHandler}
            />
        </div>
    );
};

export default App;
